import React, { useState } from 'react';
import './FuncionarioDashboard.css';

function FuncionarioDashboard() {
  const [tarefas, setTarefas] = useState([]);
  const [novaTarefa, setNovaTarefa] = useState('');

  const adicionarTarefa = (e) => {
    e.preventDefault();
    if (novaTarefa.trim()) {
      setTarefas([...tarefas, { id: Date.now(), texto: novaTarefa, concluida: false }]);
      setNovaTarefa('');
    }
  };

  const toggleTarefa = (id) => {
    setTarefas(tarefas.map(tarefa =>
      tarefa.id === id ? { ...tarefa, concluida: !tarefa.concluida } : tarefa
    ));
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Painel do Funcionário</h1>
        <button className="logout-button">Sair</button>
      </header>

      <div className="dashboard-content">
        <section className="tarefas-section">
          <h2>Minhas Tarefas</h2>
          <form onSubmit={adicionarTarefa} className="tarefa-form">
            <input
              type="text"
              value={novaTarefa}
              onChange={(e) => setNovaTarefa(e.target.value)}
              placeholder="Adicionar nova tarefa"
            />
            <button type="submit">Adicionar</button>
          </form>

          <ul className="tarefas-lista">
            {tarefas.map(tarefa => (
              <li key={tarefa.id} className={`tarefa-item ${tarefa.concluida ? 'concluida' : ''}`}>
                <input
                  type="checkbox"
                  checked={tarefa.concluida}
                  onChange={() => toggleTarefa(tarefa.id)}
                />
                <span>{tarefa.texto}</span>
              </li>
            ))}
          </ul>
        </section>

        <section className="info-section">
          <h2>Informações</h2>
          <div className="info-card">
            <h3>Horário de Trabalho</h3>
            <p>Entrada: 08:00</p>
            <p>Saída: 17:00</p>
          </div>
          <div className="info-card">
            <h3>Próximos Eventos</h3>
            <p>Nenhum evento agendado</p>
          </div>
        </section>
      </div>
    </div>
  );
}

export default FuncionarioDashboard;
